sp_widget.client_script:
function($scope,$sce) {
  /* widget controller */
	  var c = this;

	c.submit = function(){

		if((!c.data.firstName)||(!c.data.lastName)||(!c.data.title)||(!c.data.email)||(!c.data.phone)||(!c.data.hospital)||(!c.data.presentation_title)||(!c.data.length)||(!c.data.app)||(!c.data.audience)||(!c.data.shortdescription)||(!c.data.details)||(!c.data.disclose)||(!c.data.comments)){
			alert("Please fill out all fields before submitting");
			return false;
		}
		c.data.additionals = $scope.choices;
	  c.server.update().then(function(){
			setTimeout(function(){
				window.location.href = 'swug19';
			}, 2500);

					swal({
  type: 'success',
  title: 'Your presentation has been submitted',
  showConfirmButton: false,
  timer: 2500
}).then(function(){
						window.location.href = 'swug19';

					});


		});



	}



	 $scope.choices = [{"id": 1,"type":"name","title":""}];


  $scope.index = $scope.choices.length;

  $scope.addNewChoice = function() {
    var newItemNo = ++$scope.index;
    $scope.choices.push({'id':newItemNo, "type":"name","title":""});
    c.data.test = $scope.choices;

  };

  $scope.removeChoice = function(id) {

        if($scope.choices.length<=1){
            alert("input cannot be less than 1");
            return;
        }


        var index = -1;
   var comArr = eval( $scope.choices );
   for( var i = 0; i < comArr.length; i++ ) {
   if( comArr[i].id === id) {
   index = i;
   break;
   }
		}
   if( index === -1 ) {
   alert( "Something gone wrong" );
		}
    $scope.choices.splice( index, 1 );
  };




	}

