sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */

data.is_logged_in = gs.getSession().isLoggedIn();

if(input){

	var se = createSession();
	createUser(se);
	createAdditionalUsers(se);

	gs.addInfoMessage("Thank you " + input.firstName + " " + input.lastName + ". Your presentation has been submitted");

}



})();
function createAdditionalUsers(session){
	var addUser = input.additionals;
	for(i=0; i<addUser.length; i++){
	if(addUser[i].firstName){
	var us = new GlideRecord('x_sthc_swug_19_swug_users_extended');
	us.initialize();
	us.first_name = addUser[i].firstName;
	us.last_name = addUser[i].lastName;
	us.email = addUser[i].email;
	us.swug_role = addUser[i].title;
	us.hospital = addUser[i].organization;
	us.password_needs_reset = 'true';
	us.user_name = addUser[i].email;

	var user = us.insert();
	createMap(user,session)

	var rl = new GlideRecord('sys_user_has_role');
	rl.initialize();
	rl.setValue('user',user);
	rl.setValue('role','940ba702933002009c8579b4f47ffbe2');
	rl.insert();
	}

	}
}

function createUser(session){
	var us = new GlideRecord('x_sthc_swug_19_swug_users_extended');
	us.initialize();
	us.first_name = input.firstName;
	us.last_name = input.lastName;
	us.email = input.email;
	us.mobile_phone = input.phone
	us.hospital = input.hospital;
	us.swug_role = input.title;
	us.password_needs_reset = 'true';
	us.user_name = input.email;

	var user = us.insert();
	createMap(user,session)

	var rl = new GlideRecord('sys_user_has_role');
	rl.initialize();
	rl.setValue('user',user);
	rl.setValue('role','940ba702933002009c8579b4f47ffbe2');
	rl.insert();

	gs.eventQueue('x_sthc_swug_19.SWUG.New.Session',data.session,user);

}

function createSession(){
	var ses = new GlideRecord('x_sthc_swug_19_swug19_sessions');
	ses.initialize();
	ses.speaker = input.firstName + " " + input.lastName;
	ses.presenting_hospital = input.hospital;
	ses.topic = input.presentation_title;
	ses.length_of_presentation = input.length;
	ses.application = input.app.toString();
	ses.role = input.audience.toString();
	ses.abstract = input.shortdescription;
	ses.detail = input.details;
	ses.conflict = input.disclose;
	//ses.cec = input.cec;
	ses.comments = input.comments;
	var session = ses.insert();
	data.session = ses;
	return session;

}

function createMap(user,session){
	var map = new GlideRecord('x_sthc_swug_19_speaker_map');
	map.setValue('session',session);
	map.setValue('speaker',user);
	map.insert();

}
