})();

function checkUser(){
	var gr = new GlideRecord("x_sthc_swug_19_swug_users_extended");
	gr.addQuery('email',input.email);
	gr.query();
	while(gr.next()){
		data.found = true;
	gr.user_password.setDisplayValue("SWUG19");
  gr.password_needs_reset = 'true';
	gr.update();
	}

}
